import  React,{ useState } from 'react';
import Header from "./components/Header";
import ProductLs from "./components/ProductLs"
import Swal from 'sweetalert2'

function App() {
  const [products,setProducts]= useState([
    {
      id:1,
      name:'x',
      price:699,
      image:'https://cdn.pixabay.com/photo/2016/03/24/11/28/copper-kettle-1276542__340.jpg',
    },
    {
      id:2,
      name:'y',
      price:699,
      image:'https://cdn.pixabay.com/photo/2012/12/12/19/07/crockery-69606__340.jpg',
    },,
    {
      id:3,
      name:'z',
      price:699,
      image:'https://cdn.pixabay.com/photo/2013/07/13/11/43/frying-158521__340.png',
    },
    {
      id:4,
      name:'k',
      price:699,
      image:'https://cdn.pixabay.com/photo/2020/02/21/12/50/kitchen-4867419__340.jpg',
    },
    {
      id:5,
      name:'m',
      price:699,
      image:'https://cdn.pixabay.com/photo/2018/07/12/02/01/dishes-3532396_960_720.jpg',
    },
    {
      id:6,
      name:'b',
      price:699,
      image:'https://cdn.pixabay.com/photo/2019/09/22/19/40/cup-4496839_960_720.jpg',
    }

  ]);
  const [cartItems,setCartItems]=useState([]);
  const addToCart = (item)=>{
    let productItem = cartItems.find(product=>product.id===item.id); 
    if(productItem){
      productItem.quantity +=1;
      setCartItems([...cartItems]);
      Swal.fire({
       position: 'top-end',
       icon: 'success',
       title: 'Your item has been updated',
      showConfirmButton: false,
      timer: 1500
});


    }else{
      item.quantity =1;
      setCartItems([item,...cartItems]);
      Swal.fire({
       position: 'top-end',
       icon: 'success',
       title: 'Your item has been saved',
      showConfirmButton: false,
      timer: 1500
});

    }

  }
  return (
    <div className="container">
      < Header/>
      <ProductLs products={products} addToCart={addToCart}/>






    </div>
  );
}

export default App;
